
document.addEventListener('DOMContentLoaded',function(){var burger=document.getElementsByClassName('header__burger')[0];var close=document.getElementsByClassName('header__close')[0];var nav=document.getElementsByClassName('header__nav')[0];var button=document.getElementsByClassName('header__button')[0];close.addEventListener('click',function(){nav.classList.add('header__none');close.classList.add('header__none');burger.classList.remove('header__none');})
burger.addEventListener('click',function(){nav.classList.remove('header__none');close.classList.remove('header__none');burger.classList.add('header__none');})
function onMedia(query){if(query.matches){nav.classList.remove('header__none');close.classList.add('header__none');burger.classList.add('header__none');button.classList.remove('header__none');}else{nav.classList.add('header__none');close.classList.add('header__none');burger.classList.remove('header__none');button.classList.add('header__none');}}
var query=window.matchMedia("(min-width: 1024px)")
onMedia(query);query.addEventListener("change",function(){onMedia(query);});})